print("length of dictionary:", len(colours))

words = list(colours.values())

print("Words:", words)
# Count occurrences of each substring
substring_counts = defaultdict(int)

for word in words:
    for i in range(len(word)):
        for j in range(i + 1, len(word) + 1):
            substring = word[i:j]
            substring_counts[substring] += 1

# get the top 5 substring with the highest count for each size substring

top_substrings = defaultdict(list)
for substring, count in substring_counts.items():
    size = len(substring)
    top_substrings[size].append((count, substring))

for size, substrings in top_substrings.items():
    top_substrings[size] = sorted(substrings, reverse=True)[:5]

print("Top substrings:")
for size, substrings in top_substrings.items():
    print(f"Size {size}:")
    for count, substring in substrings:
        print(f"  {substring}: {count}")

